const up = 40;

pub fn main() !void {
    var counter: i64 = 0;
    const stdout = std.io.getStdOut().writer();

    for (input) |c, index| {
        if (c == up) {
            counter = counter + 1;
        } else {
            counter = counter - 1;
        }

        if (counter == -1) {
            try stdout.print("Enter basement {d}\n", .{index + 1});
        }
    }
    try stdout.print("Floor {d}\n", .{counter});
}
